(function ($) {
	'use strict';
	$(document).ready(function () {

		$('.click').click(function (event) {

			if ($(this).children('.detail').is(':visible')) {

				$(this).children('.detail').fadeOut();

			} else {

				$('.detail').fadeOut();
				$(this).children('.detail').fadeIn();
			}

			event.stopPropagation();
		});

		$(document).click(function (event) {

			$('.detail').fadeOut();

			event.stopPropagation();
		});

		$('.picker').click(kamaDatepicker('date', {

			markToday: true,
			markHolidays: true,
			gotoToday: true,
			highlightSelectedDay: true,
			sync: true,
			closeAfterSelect: true,
			nextButtonIcon: "icon-line icon-arrow-right-circle",
			previousButtonIcon: "icon-line icon-arrow-left-circle"
		}));
	});
})(jQuery);